function printMatches(matchedItems) {
  let markdown = '';

  for (let index = 0; index < matchedItems.length; index++) {
    const item = matchedItems[index];
    const intervention = item.originItem;
    const matchResult = item.matchResult;
    const matchedDosage = item.matchedDosage;
    const reason = matchResult.recommendationReason ? matchResult.recommendationReason : matchResult.notRecommendationReason;

    markdown += `**${index + 1}. ${intervention.name}**\n`;

    if (reason) {
      markdown += `  - Reason: ${reason}\n`;
    }

    const retries = item.validationResults.length - 1;

    markdown += `  - Retries: ${retries}\n`;

    if (!matchResult.isRecommended) {
      markdown += '\n';
      continue;
    }

    if (matchResult.option) {
      markdown += `  - Option: ${matchResult.option}\n`;
    }

    if (matchedDosage) {
      markdown += `  - Dosage: ${matchedDosage.dosage}\n`;
      markdown += `  - Dosage Frequency: ${matchedDosage.frequency}\n`;

      if (matchedDosage.option) {
        markdown += `  - Dosage option: ${matchedDosage.option}\n`;
      }
    }
    else {
      markdown += `  - Dosage: not matched\n`;
      markdown += `  - Not Matched Dosage from: ${matchResult.dosage.dosage}\n`
    }

    if (matchResult.recommendationReason) {
      markdown += `  - Dosage recommendation reason: ${matchResult.recommendationReason}\n`
    }

    if (intervention.dossingInstructions) {
      markdown += `  - Dossign instructions: ${intervention.dossingInstructions}\n`;
    }

    markdown += '\n';
  }

  return markdown;
}

