markdown += '# Health Plan\n\n';
markdown += `Generated at: ${DateTime.fromISO(data.generatedAt).toLocaleString(DateTime.DATETIME_SHORT_WITH_SECONDS)}\n`;
markdown += `Execution time: ${data.executionTime}\n\n`;

markdown += '## Matched items\n\n';

const matchedItems = data.interventionItems.filter(x => x.matchResult.isRecommended);

if (matchedItems.length > 0) {
  markdown += printMatches(matchedItems);
} else {
  markdown += '**No matches**\n';
}

markdown += '\n';

markdown += '## NOT Matched items\n\n';

const notMatchedItems = data.interventionItems.filter(x => !x.matchResult.isRecommended);

if (notMatchedItems.length > 0) {
  markdown += printMatches(notMatchedItems);
} else {
  markdown += '**No matches**\n';
}

return [
  {
    markdown
  }
];

