function reverseFlat(array, maxItems) {
    const result = [];
    const maxRowLength = Math.max(...array.map(x => x.results.length));

    for (let i = 0; i < maxRowLength; ++i) {
        for (let j = 0; j < array.length; ++j) {
            if (i >= array[j].results.length)
                continue;

            const item = array[j].results[i];

            if (result.find(x => x.item.documentId == item.documentId))
                continue;

            result.push({
              query: array[j].query,
              item
            });

            if (result.length == maxItems)
                return result;
        }
    }

    return result;
}

